import {LitElement, html, css} from 'lit';
import {customElement} from 'lit/decorators.js';
import {SignalWatcher, signal, watch} from '@lit-labs/signals';

const log = signal<unknown[][]>([]);

const oldLog = window.console.log
window.console.log = (...values: unknown[]) => {
  log.set([...log.get(), values]);
  oldLog(...values)
};

const globalStyles = new CSSStyleSheet();

globalStyles.replaceSync(`body {
  display: flex;
}

body > * {
  flex: 1
}`)

document.adoptedStyleSheets.push(globalStyles);

@customElement('visual-logger')
export class MyElement extends SignalWatcher(LitElement) {
  render() {
    return html`
      <div id="log">
        <b>Log:</b>
        <hr/>
          ${log.get().reverse().map(val => html`<p>${val.join(' ')}</p>`)}
      </div>
    `;
  }

  static styles = css`
    #log {
      display: block;
      max-height: 100dvh;
    }

    p {
      margin: 0;
      font-family: monospace;
    }
  `;
}
